mapper.select:
SELECT		COUNT(SITE_CD)
FROM 		COM_SITE
WHERE		SITE_CD = #{siteCd}

mapper.insert:
INSERT 	INTO COM_SITE
(
		  SITE_CD
		, SITE_NM
		, SITE_ENG_NM
		, SITE_ADDR
		, SITE_ENG_ADDR
		, TEL_NO
		, FAX_NO
		, SITE_INFO01
		, SITE_INFO02
		, SITE_INFO03
		, SITE_INFO04
		, SITE_INFO05
		, SITE_INFO06
		, SITE_INFO07
		, SITE_INFO08
		, SITE_INFO09
		, SITE_INFO10
		, INPT_ID
		, INPT_IP
		, INPT_DTTM
		, MODI_ID
		, MODI_IP
		, MODI_DTTM
		, SYS_ACCESS_YN
		, LOGIN_YN
		, USE_YN
		, TEMPLATE_CD
		, SITE_META
) VALUES (
		  #{siteCd}
		, #{siteNm}
		, #{siteEngNm}
		, #{siteAddr}
		, #{siteEngAddr}
		, #{telNo}
		, #{faxNo}
		, #{siteInfo01}
		, #{siteInfo02}
		, #{siteInfo03}
		, #{siteInfo04}
		, #{siteInfo05}
		, #{siteInfo06}
		, #{siteInfo07}
		, #{siteInfo08}
		, #{siteInfo09}
		, #{siteInfo10}
		, #{inptId}
		, #{inptIp}
		, NOW()
		, #{inptId}
		, #{inptIp}
		, NOW()
		, #{sysAccessYn}
		, #{loginYn}
		, #{useYn}
		, #{templateCd}
		, #{siteMeta}
)

mapper.update:
UPDATE		COM_SITE
SET
			  SITE_NM = #{siteNm}
			, SITE_ENG_NM = #{siteEngNm}
			, SITE_ADDR = #{siteAddr}
			, SITE_ENG_ADDR = #{siteEngAddr}
			, TEL_NO = #{telNo}
			, FAX_NO = #{faxNo}
			, SITE_INFO01 = #{siteInfo01}
			, SITE_INFO02 = #{siteInfo02}
			, SITE_INFO03 = #{siteInfo03}
			, SITE_INFO04 = #{siteInfo04}
			, SITE_INFO05 = #{siteInfo05}
			, SITE_INFO06 = #{siteInfo06}
			, SITE_INFO07 = #{siteInfo07}
			, SITE_INFO08 = #{siteInfo08}
			, SITE_INFO09 = #{siteInfo09}
			, SITE_INFO10 = #{siteInfo10}
			, SYS_ACCESS_YN = #{sysAccessYn}
			, LOGIN_YN = #{loginYn}
			, USE_YN = #{useYn}
			, TEMPLATE_CD = #{templateCd}
			, SITE_META = #{siteMeta}
			, MODI_ID = #{modiId}
			, MODI_IP = #{modiIp}
			, MODI_DTTM = NOW()
WHERE 		SITE_CD = #{siteCd}

UPDATE
		COM_SITE
SET
		SITE_LOGO = #{siteLogo}
WHERE	SITE_CD = #{siteCd}
