mapper.select:
SELECT
			COUNT(CMNT_ID)
FROM		COM_BBS_CMNT
WHERE		BBS_CD = #{bbsCd}
AND			BBS_ID = #{bbsId}

mapper.insert:

	SELECT 'CMNT_'||LPAD(NVL(MAX(CMNT_SEQ), 0)+1, 10, 0) FROM COM_BBS_CMNT

INSERT
INTO 	COM_BBS_CMNT
(
		  CMNT_ID
		, BBS_ID
		, BBS_CD
		, CMNT_TXT
		, WRITER
		, PW
		, CMNT_SEQ
		, INPT_ID
		, INPT_IP
		, INPT_DTTM
) VALUES (
		  #{cmntId}
		, #{bbsId}
		, #{bbsCd}
		, #{cmntTxt}
		, #{writer}
		, #{pw}
		, (SELECT NVL(MAX(CMNT_SEQ), 0)+1 FROM COM_BBS_CMNT)
		, #{inptId}
		, #{inptIp}
		, SYSDATE
)

mapper.update:
UPDATE
		COM_BBS_CMNT
SET
		CMNT_TXT = #{cmntTxt}
WHERE	BBS_CD = #{bbsCd}
AND		BBS_ID = #{bbsId}
AND		CMNT_ID = #{cmntId}

mapper.delete:
DELETE 
FROM		COM_BBS_CMNT
WHERE		BBS_CD = #{bbsCd}
AND			BBS_ID = #{bbsId}
AND			CMNT_ID = #{cmntId}

DELETE 
FROM		COM_BBS_CMNT
WHERE		BBS_CD = #{bbsCd}
AND			BBS_ID = #{bbsId}
