mapper.insert:

	SELECT NVL(MAX(ABS(CERT_UID)), 0) + 1 FROM COM_CERT_LOG

INSERT
INTO	 COM_CERT_LOG
(
		  CERT_UID
		, CERT_TYPE
		, CERT_KEY
		, CERT_RESULT
		, CERT_TRGT
		, CERT_DTTM
		, INPT_ID
		, INPT_IP
		, INPT_DTTM
) VALUES (
		  #{certUid}
		, #{certType}
		, #{certKey}
		, #{certResult}
		, #{certTrgt}
		, #{certDttm}
		, #{inptId}
		, #{inptIp}
		, SYSDATE
)

mapper.update:
UPDATE	COM_CERT_LOG
SET		
		  CERT_RESULT 	= #{certResult}
		, CERT_DTTM 	= SYSDATE
WHERE	CERT_UID = #{certUid}

mapper.select:
SELECT
			CASE
				WHEN CNT > 5
				THEN
						CASE
							WHEN ROUND((SYSDATE-MAX_DTTM)*24*60*60) > #{certTime} THEN 0
							ELSE #{certTime} - ROUND((SYSDATE-MAX_DTTM)*24*60*60)
						END
				ELSE 0
			END AS SEC
FROM (
			SELECT 	COUNT(CERT_UID) AS CNT, MAX(INPT_DTTM) AS MAX_DTTM
			FROM 	COM_CERT_LOG
			WHERE 	CERT_RESULT = 'N'
			AND 	CERT_TYPE = #{certification.certType}
			AND 	CERT_TRGT = #{certification.certTrgt}
			AND 	INPT_ID = #{certification.inptId}
			AND 	INPT_IP = #{certification.inptIp}
			AND 	INPT_DTTM > (
									SELECT 	NVL(MAX(INPT_DTTM), TO_DATE(TO_CHAR(SYSDATE, 'YYYYMMDD'), 'YYYYMMDD'))
									FROM 	COM_CERT_LOG
									WHERE 	CERT_RESULT = 'Y'
									AND 	CERT_TYPE = #{certification.certType}
									AND 	CERT_TRGT = #{certification.certTrgt}
									AND 	INPT_ID = #{certification.inptId}
									AND 	INPT_IP = #{certification.inptIp}
								)
) A
